INSERT INTO `wp_posts` VALUES(87, 1, '2012-08-21 10:52:30', '2012-08-21 10:52:30', 'Our work covers <a href="http://www.youngfoundation.org/node/1238">health</a>, <a href="http://www.youngfoundation.org/node/1236">ageing</a>, <a href="http://www.youngfoundation.org/node/1230">education</a>,<a href="http://www.youngfoundation.org/node/1226">communities and housing</a>, <a href="http://www.youngfoundation.org/node/1228">justice</a>, <a href="http://www.youngfoundation.org/node/1229">youth leadership</a>, creative uses of the <a href="http://www.youngfoundation.org/node/1232">web</a> and <a href="http://www.youngfoundation.org/node/1237">wellbeing</a>.\n\nWe address issues by carrying out <a href="http://www.youngfoundation.org/node/1305">research</a>, launching <a href="http://www.youngfoundation.org/node/1223">collaboratives</a>, <a href="http://www.youngfoundation.org/node/1225">creating new ventures</a>, and <a href="http://www.youngfoundation.org/node/1224">advising national and local governments and other public agencies</a>. We work both in the UK and<a href="http://www.youngfoundation.org/node/24">internationally</a>.\n\nWe collaborate with a wide range of organisations – from charities and businesses to governments and local authorities – using a comprehensive set of tools and approaches.  More information on our cross-cutting work for central government can be found <a href="http://www.youngfoundation.org/node/1208">here</a>.', 'Our Offer', '', 'inherit', 'open', 'open', '', '22-autosave', '', '', '2012-08-21 10:52:30', '2012-08-21 10:52:30', '', 22, 'http://localhost:8888/young_foundation/22-autosave/', 0, 'revision', '', 0);
